insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,8,'Poll','I sondaggi possono essere usati per testare i gusti dei tuoi utenti su vari argomenti.\n<br><br>\n\n<b>Attivo</b><br>\nSe questo box é selezionato gli utenti potranno votare. Altrimenti saranno in grado solo di visualizzare i risultati del sondaggio.\n<br><br>\n<b>Chi può votare?</b><br>\nScegli un gruppo che può votare in questo sondaggio.\n<br><br>\n<p><b>Karma Per Voto</b><br>\nQuanto karma deve essere assegnato all\'utente quando vota?</p>\n\n<b>Larghezza del grafico</b><br>\nLa larghezza del grafico dei risultati del sondaggio. La larghezza é misurata in pixel.\n<br><br>\n<b>Domanda</b><br>\nQual\'é la domanda che vorresti fare agli utenti?\n<br><br>\n<b>Risposte</b><br>\nInserisci le possibili risposte alla tua domanda. Inserisci una sola risposta per linea. I sondaggi sono capaci di gestire fino a 20 risposte possibili.\n<br><br>\n<p><b>Randomizza le risposte</b><br>\nPer essere certi che l\'ordine delle risposte non influenzi gli utenti è spesso utile prensentarle in ordine casuale ogni volta che vengono mostrate. Seleziona SI per abilitare l\'opzione</p>\n\n\n<b>Azzera i voti.</b><br>\nAzzera i voti per questo sondaggio.\n<br><br>\n\n\n', 1054311284);
