insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,8,'SyndicatedContent','Il contenuto da altri siti è contenuto che viene preso da altri siti usando le specifiche RDF/RSS. Questa tecnologia è spesso usata per estrarre titoli e sommari da vari siti di notizie, come <a href="http://www.cnn.com">CNN</a> e <a href="http://slashdot.org">Slashdot</a>. Può essere usata anche per altre cose come punteggi sportivi, informazioni finanziarie, etc. <br><br> <b>URL del file RSS </b><br> Metti qui lo URL esatto (incluso http://) del contenuto RDF o RSS. Il contenuto verrà scaricato ogni ora da questo URL. <br><br> Puoi trovare liste di contenuti disponibili in questo formato ai seguenti indirizzi:<ul> <li><a href="http://www.newsisfree.com">http://www.newsisfree.com</a> <li><a href="http://www.syndic8.com">http://www.syndic8.com</a> <li><a href="http://www.voidstar.com/node.php?id=144">http://www.voidstar.com/node.php?id=144</a> <li><a href="http://my.userland.com">http://my.userland.com</a> <li><a href="http://www.webreference.com/services/news/">http://www.webreference.com/services/news/</a> <li><a href="http://www.xmltree.com">http://www.xmltree.com</a> <li><a href="http://w.moreover.com/">http://w.moreover.com/</a> </ul> \n<p><b>Template</b><br>\nScegli un template per questo contenuto.', 1048067734);
