insert into international (internationalId,languageId,namespace,message,lastUpdated) values (50,8,'Product','I benefici sono il vantaggio che offrono le caratteristiche del prodotto. Sono la ragione per cui il tuo prodotto è buono. Se aggiungi dei benefici, dovresti inserire anche qualche caratteristica.\n\n\n\n<p>\n\n<b>Beneficio</b><br>\nPuoi inserire un nuovo beneficio, selezionarne uno da quelli già inseriti.\n<p>\n\n<b>Aggiungi un\'altro beneficio?</b><br>\nSe vuoi aggiungere un\'altro beneficio, seleziona "Si".\n<p>\n', 1046633484);
