insert into international (internationalId,languageId,namespace,message,lastUpdated) values (74,8,'Item','La seguente è la lista delle variabili disponibili nel template Item.\n<p><b>attachment.name</b><br>\nIl nome del file per il file allegato a questo item.\n<p><b>attachment.url</b><br>\nLa URL per il download del file allegato a questo item.\n<p><b>attachment.icon</b><br>\nLa URL alla icona per il file allegato a questo item.</p>\n', 1046300640);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (76,8,'FileManager','La seguente è la lista delle variabili disponibili nel template File Manager.\n<p><b>titleColumn.url</b><br>\nLa URL per ordinare per titolo.\n<p><b>titleColumn.label</b><br>\nL\'etichetta tradotta per il titolo.\n<p><b>descriptionColumn.label</b><br>\nL\'etichetta tradotta per la descrizione.\n<p><b>descriptionColumn.url</b><br>\nLa URL per ordinare per descrizione.\n<p><b>dateColumn.label</b><br>\nL\'etichetta tradotta per la data di upload.\n<p><b>dateColumn.url</b><br>\nLa URL per ordinare per data upload.\n<p><b>search.form</b><br>\nForm di ricerca di WebGUI.\n<p><b>search.url</b><br>\nLa URL per attivare o disattivare la ricerca.\n<p><b>search.label</b><br>\nL\'etichetta tradotta per il link al motore di ricerca.\n<p><b>addfile.url</b><br>\nLa URL per aggiungere un file al file manager.\n<p><b>addfile.label</b><br>\nL\'etichetta tradotta per il link aggiungi file.\n<p><b>file_loop</b><br>\nUn ciclo contenente le informazioni su ogni file caricato in questo file\nmanager.\n<blockquote>\n  <b>file.canView</b><br>\n  Una condizione per definire se l\'utente attuale ha i privilegi per vedere\n  questo file.\n  <p><b>file.controls</b><br>\n  I controlli di gestione di&nbsp; WebGUI per questo file.\n  <p><b>file.title</b><br>\n  Il titolo per questo file.\n  <p><b>file.version1.name</b><br>\n  Il nome del file per la prima versione di questo file.\n  <p><b>file.version1.url</b><br>\n  IL download URL per la prima versione di questo file.\n  <p><b>file.version1.icon</b><br>\n  La URL all\'icona per il tipo di file della prima versione di questo file.\n  <p><b>file.version1.size</b><br>\n  La dimensione di archiviazione per la prima versione di questo file.\n  <p><b>file.version1.type</b><br>\n  Il tipo (o estensione del file )&nbsp; per la prima versione di questo file.\n  <p><b>file.version1.thumbnail</b><br>\n  La URL al thumbnail per la prima versione di questo file.\n  <p><b>file.version1.isImage</b><br>\n  Una condizione che indica se la prima versione di questo file è una immagine\n  o no.\n  <p><b>file.version2.name</b><br>\n  Il nome del file per la seconda versione di questo file.\n  <p><b>file.version2.url</b><br>\n  IL download URL per la seconda versione di questo file.\n  <p><b>file.version2.icon</b><br>\n  La URL all\'icona per il tipo di file della&nbsp; seconda versione di questo\n  file.\n  <p><b>file.version2.size</b><br>\n  La dimensione di archiviazione per la seconda versione di questo file.\n  <p><b>file.version2.type</b><br>\n  Il tipo (o estensione del file ) per la seconda versione di questo file.\n  <p><b>file.version2.thumbnail</b><br>\n  La URL all\'icona per il tipo di file della seconda versione di questo file.\n  <p><b>file.version2.isImage</b><br>\n  Una condizione che indica se la seconda versione di questo file è una\n  immagine o no.\n  <p><b>file.version3.name</b><br>\n  Il nome del file per la terza versione di questo file.\n  <p><b>file.version3.url</b><br>\n  IL download URL per la terza versione di questo file.\n  <p><b>file.version3.icon</b><br>\n  La URL all\'icona per il tipo di file della terza versione di questo file.\n  <p><b>file.version3.size</b><br>\n  La dimensione di archiviazione per la terza versione di questo file.\n  <p><b>file.version3.type</b><br>\n  Il tipo (o estensione del file ) della terza versione di questo file.\n  <p><b>file.version3.thumbnail</b><br>\n  La URL al thumbnail per la terza versione di questo file.\n  <p><b>file.version3.isImage</b><br>\n  Una condizione che indica se la terza versione di questo file è una immagine\n  o no.\n  <p><b>file.description</b><br>\n  La descrizione di questo file.\n  <p><b>file.date</b><br>\n  La data in cui questo file è stato caricato.\n  <p><b>file.time</b><br>\n  Il tempo in cui questo file è stato caricato.\n  <p>&nbsp;</p>\n</blockquote>\n<p><b>noresults.message</b><br>\nUn messaggio che attesta che questo file manager non ha files da far\nvisualizzare a questo utente.\n<p><b>noresults</b><br>\nUna condizione che indica se ci sono file da visualizzare per questo utente.\n<p><b>firstPage</b><br>\nUn Link alla prima pagina dell\'impaginazione.\n<p><b>lastPage</b><br>\nUn Link all\'ultima pagina dell\'impaginazione.\n<p><b>nextPage</b><br>\nUn Link alla prossima pagina dell\'impaginazione.\n<p><b>previousPage</b><br>\nUn Link alla prossima pagina dell\'impaginazione.\n<p><b>pageList</b><br>\nUna lista di link a tutte le pagine dell\'impaginazione del calendario.\n<p><b>multiplePages</b><br>\nUna variabile che indica dove esiste più di una pagina nell\'impaginazione.</p>\n', 1046300470);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (97,8,'EventsCalendar','La seguente è la lista delle variabili disponibili nel template eventi.\n<p><b>title</b><br>\nIl titolo di questo evento.\n<p><b>start.label</b><br>\nL\'etichetta tradotta per la data di inizio.\n<p><b>start.date</b><br>\nLa data di inizio dell\'evento.\n<p><b>end.date</b><br>\nLa data di fine dell\'evento..\n<p><b>end.label</b><br>\nL\'etichetta tradotta per la data di fine.\n<p><b>canEdit</b><br>\nUna condizione che indica se l\'attuale utente può modificare un evento.\n<p><b>edit.url</b><br>\nLa URL per modificare questo evento.\n<p><b>edit.label</b><br>\nL\'etichetta tradotta per l\'URL di modifica.\n<p><b>delete.url</b><br>\nLa URL per cancellare questo evento.\n<p><b>delete.label</b><br>\nL\'etichetta tradotta per cancellare la URL.\n<p><b>previous.url</b><br>\nLa URL per visualizzare l\'evento prima di questo.\n<p><b>previous.label</b><br>\nL\'etichetta tradotta per la URL precedente.\n<p><b>next.label</b><br>\nL\'etichetta tradotta per la prossima URL.\n<p><b>next.url</b><br>\nLa URL per visualizzare l\'evento dopo di questo.\n<p><b>description</b><br>\nLa descrizione di questo evento.</p>\n', 1046297494);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (619,8,'WebGUI','Questa funzione cancella permanentemente il wobject selezionato da una pagina. Se non sei sicuro di voler cancellare questo contenuto faresti meglio a tagliare (cut) il contenuto negli appunti finché non sarai sicuro di volerlo veramente cancellare.\n<p>\n\nCome con ogni operazione di cancellazione, sei chiamato a rispondere ad una conferma per procedere con la cancellazione. Se rispondi di sì, la cancellazione procederà e non ci sarà possibilità di recuperare i dati. Se rispondi di no sarai riportato alla schermata precedente.', 1046126787);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (636,8,'WebGUI','Per creare un package segui questi passi:\n<ol>\n<li> Dal menu amministrativo scegli  "Visualizza Packages."\n<li> Aggiungi una pagina e dagli un nome. Il nome della pagina sarà il nome del package.\n<li> Vai alla nuova pagina e aggiungi pagine e widgets. Ogni pagina e widget che aggiungi sarà creata ogni volta che il package sarà sviluppato. \n</ol>\n<b>Note:</b><br>\nPer aggiungere,  modificare e cancellare i packages devi essere nel gruppo Package Mangers o nel gruppo Admins.\n<br><br>\nSe aggiungi contenuti a qualcuno dei widgets, quel contenuto sarà automaticamente copiato anche quando il package viene sviluppato.\n<br><br>\nI privilegi e gli stili assegnati alle pagine nel package non saranno copiati nello sviluppo del package. Le pagine prenderanno i privilegi e gli stili dell\'area in cui sono sviluppati.', 1046126725);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (609,8,'WebGUI','Quando cancelli uno stile tutte le pagine che usano quello stile verranno riconvertite usando lo stile fail safe (default). Per assicurare una visualizzazione coerente, dovresti essere sicuro che nessuna pagina stia usando lo stile prima di cancellarlo.\n<p>\n\nCome con ogni operazione di cancellazione, sei chiamato a rispondere ad una conferma per procedere con la cancellazione. Se rispondi di sì, la cancellazione procederà e non ci sarà possibilità di recuperare i dati. Se rispondi di no sarai riportato alla schermata precedente.', 1046126578);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (611,8,'WebGUI','<b>Nome dell\'azienda</b><br>\nTIl nome della tua azienda. Apparirà in\n tutte le email e ovunque hai usato la macro nome azienda.\n<br><br>\n<b>Ind\nirizzo email dell\'azienda</b><br>\nUn indirizzo email generale per la tua azi\nenda. Questo é l\'indirizzo dal quale tutti i messaggi automatici proverranno. P\nuò essere usato anche dal sistema di macro del C.M.S..\n<br><br>\n<b>URL dell\n\'Azienda</b><br>\nLo URL primario della tua Azienda. Questo apparirà in tutte\n le email automatiche mandate dal sistema. Può essere usato anche dal sistema di\n macro del C.M.S..\n', 1046125845);
