insert into international (internationalId,languageId,namespace,message,lastUpdated) values (625,8,'WebGUI','Carica tutte le immagini che potresti voler utilizzare in più di una posizione sul sito.\n<p>\n<b>Nome</b><br>\nL\'etichetta con la quale questa immagine verrà chiamata per l\'inclusione nelle pagine.\n<p>\n\n<b>Organizza in Cartelle</b><br>\nLa cartella Dell\'Archivio Collaterale nella quale deve essere inserita questa immagine.\n<p>\n\n<b>File</b><br>\nSeleziona un file dal tuo disco rigido da uploadare sul server.\n<p>\n\n<b>Parametri</b><br>\nAggiungi qualsiasi parametro HTML &ltimg&rt; che vuoi come default per questa immagine.\n<p>\n<i>Esempio:</i><br>\nalign="right"\nalt="Questa é una immagine"\n\n<b>Dimensione Thumbnail</b><br>\nQuanti pixel deve misurare il thumbnail di questa immagine.\n<p>\n', 1046124685);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (635,8,'WebGUI','I Package sono gruppi di pagine e widgets che sono predefinite per essere sviluppate insieme. Un package manager può sentire l\'esigenza di creare varie pagine con un forum, una FAQ, un sondaggio perché questa operazione viene eseguita spesso. I Packages sono definiti per diminuire il disturbo di azioni ripetitive.\n<br><br>\nUn package che la gente spesso crea é un package pagina/articolo. Spesso quando aggiungi una pagina vuoi anche aggiungere un articolo ad essa. Invece di fare queste operazioni una alla volta, creare la pagina, andare a quella pagina, e aggiungere l\'articolo alla pagina, troverai più veloce creare un package che faccia tutte queste cose in un colpo solo.', 1046124350);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (633,8,'WebGUI','I roots sono le pagine principali su cui si sviluppa il sito. La prima e la più importante root è la "Home" page. Molti non hanno necessità di aggiungere una nuova root, ma a qualcuno potrebbe servire. Può essere necessario creare nuove root per diverse ragioni, ad esempio per un sito in due lingue sarà necessario creare due root: italiano e inglese.', 1046123933);
