insert into international (internationalId,languageId,namespace,message,lastUpdated) values (830,8,'WebGUI','I template delle pagine sono utilizzati per posizionare i wobject nelle pagine. Puoi avere qualsiasi numero di posizione nel template della pagina, ma devi avere sempre la posizione 1. Le variabili del posizionamento nel template della pagina appaiono in questo modo:\n<br><br>\n<tmpl_var page.position1>\n<tmpl_var page.position2>\n<tmpl_var page.position3>\n<tmpl_var page.position4>\n<tmpl_var page.position5>\n<tmpl_var page.position6>\n<tmpl_var page.position7>\n<tmpl_var ...>\n<br><br>\nGli elementi di posizionamento possono salire da 1 a un miliardo, dovrebbe essere più che sufficiente per un sito medio.', 1045769428);
